import React, { Component } from "react";
import axios from 'axios'
// import { Redirect } from 'react-router-dom';

class ClientSignUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      username: '',
      password: '',
      isChef: false

    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChef = this.handleChef.bind(this);
  }

  handleChef() {
    if (this.state.isChef === false) {
      this.setState({ isChef: true })
    } else {
      this.setState({ isChef: false })
    }
  }
  handleChange(event) {
    const { name, value } = event.target
    this.setState({ [name]: value });
  }
  handleSubmit(event) {
    event.preventDefault();
    axios.post(`/register`, {
      firstname: this.state.firstname,
      lastname: this.state.lastname,
      phone: this.state.phone,
      email: this.state.email,
      username: this.state.username,
      password: this.state.password,
      isChef: this.state.isChef
    }).then(res => {
      localStorage.setItem('token', res.data.authToken.token)
      const loc = window.location.pathname
      const dir = loc.substring(0, loc.lastIndexOf('/'));
      window.location.pathname = dir + '/FindAMeal'
      // this.setState({redirect: true})
      // this.renderRedirect();
    })
  };
  // renderRedirect = () => {
  //   if (this.state.redirect) {
  //     return <Redirect to="/Login" />
  //   }
  // }

  render() {
    // if (this.state.redirect) {
    //   return <Redirect to="/FindAMeal" />
    // } else
    return (
      <div className="ClientSignUp">
        <form className="form-group" onSubmit={this.handleSubmit}>
          <label htmlFor="username">Enter your First Name: </label>
          <input
            id="firstname"
            name="firstname"
            type="text"
            value={this.state.firstname}
            onChange={this.handleChange}
          />
          <label htmlFor="username">Enter your Last Name: </label>
          <input
            id="lastname"
            name="lastname"
            type="text"
            value={this.state.lastname}
            onChange={this.handleChange}
          />
          <label htmlFor="username">Enter your Phone Number: </label>
          <input
            id="phone"
            name="phone"
            type="number"
            value={this.state.phone}
            onChange={this.handleChange}
          />
          <label htmlFor="email">Enter your email: </label>
          <input
            id="email"
            name="email"
            type="email"
            value={this.state.email}
            onChange={this.handleChange}
          />
          <label htmlFor="username">Enter your username: </label>
          <input
            id="username"
            name="username"
            type="text"
            value={this.state.username}
            onChange={this.handleChange}
          />
          <label htmlFor="password">Enter your password: </label>
          <input
            id="password"
            name="password"
            type="password"
            value={this.state.password}
            onChange={this.handleChange}
          />
          <label className="form-switch">
            <input id="isChef" type="checkbox" name="isChef" onChange={this.handleChef} />
            <i className="form-icon"></i> Are you a chef?
              </label>
          <button type="submit">Submit</button>
        </form>
      </div>
    );

  }
}

export default ClientSignUp;
